style:
:root {
	--bg: #f8f9fa;
	--text: #2d3436;
	--menu-bg: #ffffff;
	--accent: #4a90e2;
	--shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.dark-theme {
	--bg: #1a1a1a;
	--text: #ffffff;
	--menu-bg: #2d2d2d;
	--accent: #7fb6ff;
	--shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--bg);
	font-family: "Inter", sans-serif;
}

.menu-container {
	position: relative;
	width: 100px;
	height: 100px;
}

.menu-toggle {
	position: absolute;
	width: 60px;
	height: 60px;
	background: var(--menu-bg);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	box-shadow: var(--shadow);
	z-index: 2;
	left: 20px;
	top: 20px;
}

.menu-toggle i {
	font-size: 1.6rem;
	color: var(--text);
}

.social-item {
	position: absolute;
	width: 50px;
	height: 50px;
	background: var(--menu-bg);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	box-shadow: var(--shadow);
	transform: translate(25px, 25px);
	opacity: 0;
	visibility: hidden;
	cursor: pointer;
	text-decoration: none;
}

.social-item i {
	font-size: 1.2rem;
	color: var(--text);
}

.menu-active .social-item {
	opacity: 1;
	visibility: visible;
}

.menu-active .menu-toggle {
	transform: rotateZ(315deg);
}

.social-item:hover {
	transform: scale(1.1);
	background: var(--accent);
}

.social-item:hover i {
	color: var(--menu-bg);
}

.theme-switch {
	position: fixed;
	top: 30px;
	right: 30px;
	width: 50px;
	height: 50px;
	border-radius: 50%;
	background: var(--menu-bg);
	border: none;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
	box-shadow: var(--shadow);
}

.theme-switch i {
	font-size: 1.3rem;
	color: var(--text);
}

@media (max-width: 480px) {
	.menu-container {
		transform: scale(0.8);
	}
}

script:
const menuToggle = document.getElementById("menuToggle");
const menuContainer = document.querySelector(".menu-container");
const socialItems = document.querySelectorAll(".social-item");
const themeSwitch = document.getElementById("themeSwitch");
const body = document.body;

menuToggle.addEventListener("click", () => {
	menuContainer.classList.toggle("menu-active");
	const radius = 100;
	const angle = (2 * Math.PI) / socialItems.length;

	socialItems.forEach((item, index) => {
		if (menuContainer.classList.contains("menu-active")) {
			const x = radius * Math.cos(angle * index);
			const y = radius * Math.sin(angle * index);
			item.style.transform = `translate(${x + 25}px, ${y + 25}px)`;
		} else {
			item.style.transform = "translate(25px, 25px)";
		}
	});
});

function toggleTheme() {
	body.classList.toggle("dark-theme");
	const isDark = body.classList.contains("dark-theme");
	themeSwitch.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
	localStorage.setItem("theme", isDark ? "dark" : "light");
}

const savedTheme = localStorage.getItem("theme");
const systemDark = window.matchMedia("(prefers-color-scheme: dark)").matches;

if (savedTheme === "dark" || (!savedTheme && systemDark)) {
	body.classList.add("dark-theme");
	themeSwitch.innerHTML = '<i class="fas fa-sun"></i>';
}

themeSwitch.addEventListener("click", toggleTheme);
