style:
:root {
	--bg: #ffffff;
	--text: #2d3436;
	--icon-bg: #f8f9fa;
	--gradient: linear-gradient(45deg, #4bc0c8, #c779d0, #feac5e);
}

body[data-theme="dark"] {
	--bg: #1a1a1a;
	--text: #ffffff;
	--icon-bg: #2d2d2d;
	--gradient: linear-gradient(45deg, #7f00ff, #e100ff, #ff8b00);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s ease;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--bg);
	font-family: "Inter", sans-serif;
}

.social-container {
	display: grid;
	grid-template-columns: repeat(4, 1fr);
	gap: 2rem;
	padding: 2rem;
}

.social-link {
	width: 60px;
	height: 60px;
	display: flex;
	align-items: center;
	justify-content: center;
	position: relative;
	border-radius: 15px;
	text-decoration: none;
	background: var(--icon-bg);
}

.social-link::before {
	content: "";
	position: absolute;
	inset: -2px;
	border-radius: 17px;
	background: var(--gradient);
	z-index: -1;
	opacity: 0;
	transition: opacity 0.3s;
}

.social-link:hover::before {
	opacity: 1;
}

.social-link i {
	font-size: 1.6rem;
	color: var(--text);
	transition: color 0.3s;
}

.social-link:hover i {
	color: transparent;
	background: var(--gradient);
	-webkit-background-clip: text;
	background-clip: text;
}

.theme-toggle {
	position: fixed;
	top: 30px;
	right: 30px;
	width: 50px;
	height: 50px;
	cursor: pointer;
	opacity: 0;
	z-index: 2;
}

.theme-label {
	position: fixed;
	top: 30px;
	right: 30px;
	width: 50px;
	height: 50px;
	border-radius: 12px;
	background: var(--icon-bg);
	display: flex;
	align-items: center;
	justify-content: center;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

.theme-label i {
	font-size: 1.4rem;
	color: var(--text);
}

@media (max-width: 768px) {
	.social-container {
		grid-template-columns: repeat(2, 1fr);
		gap: 1.5rem;
	}
}

@media (max-width: 480px) {
	.social-container {
		grid-template-columns: 1fr;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const themeToggle = document.getElementById("themeToggle");
	themeToggle.addEventListener("change", () => {
		if (themeToggle.checked) {
			document.body.setAttribute("data-theme", "dark");
		} else {
			document.body.removeAttribute("data-theme");
		}
	});
});
