style:
/* Reset styles */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s, color 0.3s;
}

body {
	font-family: Arial, sans-serif;
	background: var(--bg);
	color: var(--text);
	display: flex;
	align-items: center;
	justify-content: center;
	height: 100vh;
	flex-direction: column;
	padding: 20px;
}

/* Light & Dark Theme Variables */
:root {
	--bg: #f5f5f5;
	--text: #333;
	--tab-bg: #fff;
	--border: #ddd;
	--primary: #3498db;
	--indicator: #3498db;
	--hover: rgba(0, 0, 0, 0.1);
}

.dark-mode {
	--bg: #1e1e1e;
	--text: #f5f5f5;
	--tab-bg: #292929;
	--border: #444;
	--primary: #4db8ff;
	--indicator: #4db8ff;
	--hover: rgba(255, 255, 255, 0.1);
}

/* Theme Toggle Button */
.theme-toggle {
	position: absolute;
	top: 15px;
	right: 20px;
	background: var(--tab-bg);
	border: 1px solid var(--border);
	padding: 8px 12px;
	border-radius: 20px;
	cursor: pointer;
	font-size: 16px;
	color: var(--text);
	display: flex;
	align-items: center;
	gap: 8px;
}

.theme-toggle:hover {
	background: var(--hover);
}

/* Tab Container */
.tabs {
	width: 100%;
	max-width: 600px;
	background: var(--tab-bg);
	padding: 20px;
	border-radius: 10px;
	box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
	border: 1px solid var(--border);
	text-align: center;
}

/* Tab Buttons */
.tab-header {
	display: flex;
	position: relative;
	border-bottom: 2px solid var(--border);
}

.tab-header button {
	flex: 1;
	background: none;
	border: none;
	color: var(--text);
	padding: 15px;
	font-size: 16px;
	cursor: pointer;
	display: flex;
	flex-direction: column;
	align-items: center;
	gap: 5px;
	position: relative;
	transition: color 0.3s;
}

.tab-header button i {
	font-size: 20px;
}

.tab-header button:hover {
	color: var(--primary);
}

/* Sliding Indicator */
.indicator {
	position: absolute;
	bottom: 0;
	height: 4px;
	background: var(--indicator);
	width: 0;
	left: 0;
	transition: 0.3s;
}

/* Tab Content */
.tab-content > div {
	display: none;
	animation: fadeIn 0.3s ease-in-out;
	padding: 20px;
}

.tab-content > div.active {
	display: block;
}

@keyframes fadeIn {
	from {
		opacity: 0;
	}
	to {
		opacity: 1;
	}
}

/* Responsive */
@media (max-width: 480px) {
	.tabs {
		width: 100%;
	}

	.tab-header button {
		font-size: 14px;
		padding: 10px;
	}

	.tab-header button i {
		font-size: 18px;
	}
}

script:
document.addEventListener("DOMContentLoaded", function () {
	const tabButtons = document.querySelectorAll(".tab-header button");
	const tabPanels = document.querySelectorAll(".tab-content > div");
	const indicator = document.querySelector(".indicator");
	const themeToggle = document.querySelector(".theme-toggle");
	const body = document.body;

	// Handle Tab Switching
	function updateIndicator(button) {
		const buttonRect = button.getBoundingClientRect();
		const parentRect = button.parentElement.getBoundingClientRect();
		indicator.style.width = `${buttonRect.width}px`;
		indicator.style.left = `${buttonRect.left - parentRect.left}px`;
	}

	tabButtons.forEach((button) => {
		button.addEventListener("click", function () {
			tabButtons.forEach((btn) => btn.classList.remove("active"));
			tabPanels.forEach((panel) => panel.classList.remove("active"));

			this.classList.add("active");
			document.getElementById(this.getAttribute("data-tab")).classList.add("active");
			updateIndicator(this);
		});
	});

	// Initialize Indicator Position
	updateIndicator(document.querySelector(".tab-header button.active"));

	// Theme Toggle Function
	themeToggle.addEventListener("click", function () {
		body.classList.toggle("dark-mode");

		if (body.classList.contains("dark-mode")) {
			themeToggle.innerHTML = '<i class="fa-solid fa-sun"></i> Light Mode';
		} else {
			themeToggle.innerHTML = '<i class="fa-solid fa-moon"></i> Dark Mode';
		}
	});

	window.addEventListener("resize", () => {
		updateIndicator(document.querySelector(".tab-header button.active"));
	});
});
