style:
*:before,
*:after,
*,
::after,
::before {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

.tab-container {
	max-width: 768px;
	margin: 150px auto;
	border-radius: 12px;
	background-color: grey;
}

.tabs {
	display: flex;
	align-items: center;
	justify-content: space-around;
}

.tabs input {
	opacity: 0;
	visibility: hidden;
	display: none;
}

.tabs label {
	flex: 1;
	text-align: center;
	color: wheat;
	border: 1px solid grey;
	background-color: #333;
	cursor: pointer;
	padding: 10px;
	transition: all 0.4s ease;
}

.tabs label:hover {
	background-color: #1f1e1e;
}

.tabs input:checked + label {
	background: grey;
	color: white;
}

.tab-content {
	display: none;
	padding: 15px;
	border-radius: 5px;
	min-height: 200px;
}

.tab-content h2,
.tab-content p {
	margin: 18px 0;
	color: aliceblue;
}

.tab-container:has(#tab-1:checked) #tab-content-1,
.tab-container:has(#tab-2:checked) #tab-content-2,
.tab-container:has(#tab-3:checked) #tab-content-3,
.tab-container:has(#tab-4:checked) #tab-content-4 {
	display: block;
}
