style:
:root {
	--bg-color: #e7e7e7;
	--text-color: #777;
	--tab-bg: #fff;
	--label-bg: #f2f2f2;
	--label-hover-bg: #f9f9f9;
	--border-color: #eee;
	--shadow-color: rgba(0, 0, 0, 0.12);
	--tab-wrap-border-radius: 6px;
}

[data-theme="dark"] {
	--bg-color: #333;
	--text-color: #ddd;
	--tab-bg: #444;
	--label-bg: #555;
	--label-hover-bg: #666;
	--border-color: #666;
	--shadow-color: rgba(255, 255, 255, 0.1);
}

body {
	font-family: "Helvetica", sans-serif;
	background-color: var(--bg-color);
	color: var(--text-color);
	padding: 30px 0;
	font-weight: 300;
	transition: background-color 0.3s, color 0.3s;
}

.container {
	margin: 0 auto;
	display: block;
	max-width: 800px;
}

.container > *:not(.tab-wrap) {
	padding: 0 80px;
}

.tab-wrap {
	transition: 0.3s box-shadow ease;
	border-radius: var(--tab-wrap-border-radius);
	max-width: 100%;
	display: flex;
	flex-wrap: wrap;
	position: relative;
	background-color: var(--tab-bg);
	margin: 40px 0;
	box-shadow: 0 1px 3px var(--shadow-color), 0 1px 2px var(--shadow-color);
}

.tab-wrap:hover {
	box-shadow: 0 12px 23px var(--shadow-color), 0 10px 10px var(--shadow-color);
}

.tab {
	display: none;
}

.tab + label {
	box-shadow: 0 -1px 0 var(--border-color) inset;
	border-radius: var(--tab-wrap-border-radius) var(--tab-wrap-border-radius) 0 0;
	cursor: pointer;
	display: block;
	color: var(--text-color);
	flex-grow: 3;
	text-align: center;
	background-color: var(--label-bg);
	user-select: none;
	padding: 15px;
	height: 50px;
	box-sizing: border-box;
	transition: 0.3s background-color ease, 0.3s box-shadow ease;
}

.tab + label:hover {
	background-color: var(--label-hover-bg);
	box-shadow: 0 1px 0 #f4f4f4 inset;
}

.tab:checked + label {
	background-color: var(--tab-bg);
	box-shadow: 0 -1px 0 var(--tab-bg) inset;
}

.tab:checked + label:hover {
	background-color: var(--tab-bg);
}

.tab-content {
	padding: 10px 25px;
	background-color: transparent;
	position: absolute;
	width: 100%;
	z-index: -1;
	opacity: 0;
	left: 0;
	transform: translateY(-3px);
	border-radius: var(--tab-wrap-border-radius);
}

#tab1:checked ~ .tab-content:nth-of-type(1),
#tab2:checked ~ .tab-content:nth-of-type(2),
#tab3:checked ~ .tab-content:nth-of-type(3) {
	opacity: 1;
	transition: 0.5s opacity ease-in, 0.8s transform ease;
	position: relative;
	top: 0;
	z-index: 100;
	transform: translateY(0);
	text-shadow: 0 0 0;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	padding: 10px;
	background: var(--label-bg);
	border: none;
	border-radius: 5px;
	cursor: pointer;
	color: var(--text-color);
}

h1,
h2,
h3 {
	color: var(--text-color);
}

script:
function toggleTheme() {
	const currentTheme = document.documentElement.getAttribute("data-theme");
	const newTheme = currentTheme === "dark" ? "light" : "dark";
	document.documentElement.setAttribute("data-theme", newTheme);
}
