style:
:root {
	--bg-color: #ffffff;
	--text-color: #2d3436;
	--primary-color: #4a90e2;
	--secondary-color: #f8f9fa;
	--accent-color: #ff4757;
	--border-radius: 8px;
	--transition-speed: 0.3s;
}

[data-theme="dark"] {
	--bg-color: #1a1a1a;
	--text-color: #f8f9fa;
	--primary-color: #6c5ce7;
	--secondary-color: #2d3436;
	--accent-color: #ff6b6b;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all var(--transition-speed) ease;
}

body {
	background: var(--bg-color);
	color: var(--text-color);
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 2rem;
}

.theme-controls {
	position: fixed;
	top: 1rem;
	right: 1rem;
	display: flex;
	gap: 0.5rem;
}

.theme-btn {
	background: var(--secondary-color);
	border: none;
	padding: 0.8rem;
	border-radius: 50%;
	cursor: pointer;
	box-shadow: 0 2px 8px var(--shadow-color);
	display: flex;
	align-items: center;
	justify-content: center;
}

.theme-btn i {
	font-size: 1.2rem;
	color: var(--text-color);
}

.vertical-tabs {
	display: flex;
	background: var(--bg-color);
	border-radius: var(--border-radius);
	box-shadow: 0 8px 24px var(--shadow-color);
	width: 100%;
	max-width: 1000px;
	min-height: 400px;
	border: 1px solid white;
}

.tab-sidebar {
	width: 250px;
	background: var(--secondary-color);
	padding: 1.5rem;
	border-radius: var(--border-radius) 0 0 var(--border-radius);
}

.tab-nav {
	display: flex;
	flex-direction: column;
	gap: 0.5rem;
}

.tab-btn {
	background: var(--bg-color);
	border: none;
	padding: 1rem 1.5rem;
	cursor: pointer;
	display: flex;
	align-items: center;
	gap: 1rem;
	font-size: 1rem;
	color: var(--text-color);
	border-radius: var(--border-radius);
	position: relative;
}

.tab-btn:hover {
	transform: translateX(5px);
}

.tab-btn.active-tab {
	background: var(--primary-color);
	color: white;
	box-shadow: 4px 0 12px rgba(74, 144, 226, 0.3);
}

.badge {
	position: absolute;
	right: 1rem;
	background: var(--accent-color);
	color: white;
	padding: 0.2rem 0.5rem;
	border-radius: 12px;
	font-size: 0.8rem;
}

.tab-content {
	flex: 1;
	padding: 2rem;
	display: none;
	opacity: 0;
	animation: fadeIn 0.4s ease forwards;
}

.tab-content.active-content {
	display: block;
	opacity: 1;
}

.tab-content h2 {
	color: var(--primary-color);
	margin-bottom: 1.5rem;
	font-size: 1.75rem;
}

.tab-content p {
	line-height: 1.6;
	margin-bottom: 1rem;
}

@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateY(10px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@media (max-width: 768px) {
	.vertical-tabs {
		flex-direction: column;
		min-height: auto;
	}

	.tab-sidebar {
		width: 100%;
		border-radius: var(--border-radius) var(--border-radius) 0 0;
	}

	.tab-btn:hover {
		transform: translateY(5px);
	}

	.tab-content {
		padding: 1.5rem;
	}
}

script:
const themeToggle = document.getElementById("themeToggle");
const colorChange = document.getElementById("colorChange");
const body = document.body;
const accentColors = ["#ff4757", "#2ed573", "#ffa502", "#1e90ff"];

function toggleTheme() {
	const isDark = body.getAttribute("data-theme") === "dark";
	body.setAttribute("data-theme", isDark ? "light" : "dark");
	themeToggle.innerHTML = isDark ? '<i class="fas fa-moon"></i>' : '<i class="fas fa-sun"></i>';
	localStorage.setItem("theme", isDark ? "light" : "dark");
}

function changeAccentColor() {
	const randomColor = accentColors[Math.floor(Math.random() * accentColors.length)];
	document.documentElement.style.setProperty("--primary-color", randomColor);
}

const savedTheme = localStorage.getItem("theme");
if (savedTheme) {
	body.setAttribute("data-theme", savedTheme);
	themeToggle.innerHTML =
		savedTheme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
}

themeToggle.addEventListener("click", toggleTheme);
colorChange.addEventListener("click", changeAccentColor);

document.querySelectorAll(".tab-btn").forEach((button) => {
	button.addEventListener("click", () => {
		document.querySelectorAll(".tab-btn").forEach((btn) => btn.classList.remove("active-tab"));
		document
			.querySelectorAll(".tab-content")
			.forEach((content) => content.classList.remove("active-content"));
		button.classList.add("active-tab");
		document.getElementById(button.dataset.tab).classList.add("active-content");
	});
});

document.addEventListener("keydown", (e) => {
	const tabs = Array.from(document.querySelectorAll(".tab-btn"));
	const currentIndex = tabs.findIndex((tab) => tab.classList.contains("active-tab"));

	if (e.key === "ArrowDown" || e.key === "ArrowRight") {
		const nextIndex = (currentIndex + 1) % tabs.length;
		tabs[nextIndex].click();
	}

	if (e.key === "ArrowUp" || e.key === "ArrowLeft") {
		const prevIndex = (currentIndex - 1 + tabs.length) % tabs.length;
		tabs[prevIndex].click();
	}
});
