style:
:root {
	--primary: #6366f1;
	--secondary: #a855f7;
	--accent: #ec4899;
	--background: #0f172a;
	--text: #f8fafc;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	background: var(--background);
	color: var(--text);
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 2rem;
}

.container {
	width: 100%;
	max-width: 1200px;
	position: relative;
}

.fab {
	position: fixed;
	bottom: 2rem;
	right: 2rem;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	width: 56px;
	height: 56px;
	border-radius: 50%;
	border: none;
	cursor: pointer;
	box-shadow: 0 8px 24px rgba(99, 102, 241, 0.3);
	display: flex;
	align-items: center;
	justify-content: center;
}

.fab i {
	color: white;
	font-size: 1.5rem;
}

.tabs-wrapper {
	background: rgba(15, 23, 42, 0.8);
	backdrop-filter: blur(12px);
	border-radius: 24px;
	padding: 1rem;
	box-shadow: 0 12px 32px rgba(0, 0, 0, 0.3);
}

.tabs-nav {
	display: flex;
	gap: 1rem;
	padding: 1rem;
	position: relative;
}

.tab-btn {
	background: none;
	border: none;
	padding: 1rem 2rem;
	color: rgba(248, 250, 252, 0.6);
	font-size: 1.1rem;
	cursor: pointer;
	position: relative;
	transition: all 0.3s ease;
	border-radius: 12px;
}

.tab-btn::before {
	content: "";
	position: absolute;
	inset: 0;
	border-radius: 12px;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	opacity: 0;
	transition: opacity 0.3s ease;
}

.tab-btn.active-tab {
	color: white;
	font-weight: 600;
}

.tab-btn.active-tab::before {
	opacity: 0.2;
}

.tab-btn:hover::before {
	opacity: 0.1;
}

.tabs-nav::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	height: 3px;
	background: linear-gradient(90deg, var(--primary), var(--secondary));
	transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

.tab-content {
	padding: 2rem;
	display: none;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 1.5rem;
	animation: fadeIn 0.4s ease;
}

.tab-content.active-content {
	display: grid;
}

.card {
	background: rgba(255, 255, 255, 0.05);
	border-radius: 16px;
	padding: 1.5rem;
	transition: transform 0.3s ease;
}

.card:hover {
	transform: translateY(-5px);
}

.card h3 {
	color: var(--primary);
	margin-bottom: 0.5rem;
}

.card p {
	color: rgba(248, 250, 252, 0.8);
	line-height: 1.6;
}

@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateY(10px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@media (max-width: 768px) {
	.tabs-nav {
		flex-direction: column;
	}

	.tab-content {
		grid-template-columns: 1fr;
	}

	.fab {
		bottom: 1rem;
		right: 1rem;
	}
}

script:
const tabButtons = document.querySelectorAll(".tab-btn");
const tabContents = document.querySelectorAll(".tab-content");

tabButtons.forEach((button) => {
	button.addEventListener("click", () => {
		tabButtons.forEach((btn) => btn.classList.remove("active-tab"));
		tabContents.forEach((content) => content.classList.remove("active-content"));
		button.classList.add("active-tab");
		const tabId = button.getAttribute("data-tab");
		document.getElementById(tabId).classList.add("active-content");

		const nav = document.querySelector(".tabs-nav");
		const buttonRect = button.getBoundingClientRect();
		const navRect = nav.getBoundingClientRect();

		nav.style.setProperty("--border-width", `${buttonRect.width}px`);
		nav.style.setProperty("--border-left", `${buttonRect.left - navRect.left}px`);
	});
});

document.querySelector(".fab").addEventListener("click", () => {
	const newCard = document.createElement("div");
	newCard.className = "card";
	newCard.innerHTML = `
		<h3>New Feature</h3>
		<p>Discover our latest updates and enhancements.</p>
	`;
	document.querySelector(".active-content").appendChild(newCard);
	newCard.scrollIntoView({ behavior: "smooth" });
});

function updateBorder() {
	const activeTab = document.querySelector(".tab-btn.active-tab");
	const nav = document.querySelector(".tabs-nav");

	if (activeTab) {
		const buttonRect = activeTab.getBoundingClientRect();
		const navRect = nav.getBoundingClientRect();

		nav.style.setProperty("--border-width", `${buttonRect.width}px`);
		nav.style.setProperty("--border-left", `${buttonRect.left - navRect.left}px`);
	}
}

updateBorder();
window.addEventListener("resize", updateBorder);
