style:
:root {
	--primary: #7c3aed;
	--surface: #ffffff;
	--background: #f5f3ff;
	--text: #1f2937;
	--border: #e5e7eb;
}

[data-theme="dark"] {
	--primary: #a78bfa;
	--surface: #1f2937;
	--background: #111827;
	--text: #f9fafb;
	--border: #374151;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	background: var(--background);
	color: var(--text);
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 2rem;
}

.tabs-container {
	background: var(--surface);
	width: 100%;
	max-width: 800px;
	border-radius: 16px;
	box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
	overflow: hidden;
	position: relative;
}

.progress-bar {
	position: absolute;
	top: 0;
	left: 0;
	height: 3px;
	background: var(--primary);
	width: 0;
	transition: width 0.3s ease;
	z-index: 2;
}

.tab-nav {
	display: flex;
	position: relative;
	border-bottom: 1px solid var(--border);
}

.tab-btn {
	flex: 1;
	padding: 1.5rem;
	border: none;
	background: transparent;
	cursor: pointer;
	display: flex;
	gap: 0.75rem;
	align-items: center;
	justify-content: center;
	color: var(--text);
	font-size: 1rem;
	transition: all 0.2s ease;
	position: relative;
	overflow: hidden;
}

.tab-btn::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 50%;
	width: 0;
	height: 3px;
	background: var(--primary);
	transition: all 0.3s ease;
}

.tab-btn.active-tab {
	color: var(--primary);
}

.tab-btn.active-tab::after {
	width: 100%;
	left: 0;
}

.tab-content {
	padding: 2rem;
	display: none;
	opacity: 0;
	transform: translateY(10px);
	animation: contentEnter 0.4s ease forwards;
}

@keyframes contentEnter {
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

.tab-content.active-content {
	display: block;
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	background: var(--surface);
	border: 1px solid var(--border);
	padding: 0.8rem;
	border-radius: 12px;
	cursor: pointer;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.card {
	background: rgba(124, 58, 237, 0.05);
	padding: 1.5rem;
	border-radius: 12px;
	margin-bottom: 1rem;
	border: 1px solid var(--border);
}

.card h3 {
	color: var(--primary);
	margin-bottom: 0.5rem;
}

@media (max-width: 640px) {
	.tab-nav {
		overflow-x: auto;
		-webkit-overflow-scrolling: touch;
	}

	.tab-btn {
		min-width: 140px;
		font-size: 0.9rem;
		padding: 1rem;
	}

	.tab-content {
		padding: 1.5rem;
	}
}

script:
const themeToggle = document.querySelector(".theme-toggle");
const body = document.body;

function toggleTheme() {
	const isDark = body.getAttribute("data-theme") === "dark";
	body.setAttribute("data-theme", isDark ? "light" : "dark");
	themeToggle.innerHTML = isDark ? '<i class="fas fa-moon"></i>' : '<i class="fas fa-sun"></i>';
	localStorage.setItem("theme", isDark ? "light" : "dark");
}

const savedTheme = localStorage.getItem("theme");
if (savedTheme) {
	body.setAttribute("data-theme", savedTheme);
	themeToggle.innerHTML =
		savedTheme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
}

themeToggle.addEventListener("click", toggleTheme);

const tabButtons = document.querySelectorAll(".tab-btn");
const tabContents = document.querySelectorAll(".tab-content");
const progressBar = document.querySelector(".progress-bar");

tabButtons.forEach((button) => {
	button.addEventListener("click", function () {
		progressBar.style.width = "100%";
		setTimeout(() => (progressBar.style.width = "0"), 300);

		tabButtons.forEach((b) => b.classList.remove("active-tab"));
		tabContents.forEach((c) => c.classList.remove("active-content"));

		this.classList.add("active-tab");
		document.getElementById(this.dataset.tab).classList.add("active-content");
	});
});

let touchStartX = 0;
let touchEndX = 0;
const swipeThreshold = 50;

document.querySelector(".tabs-container").addEventListener("touchstart", (e) => {
	touchStartX = e.changedTouches[0].screenX;
});

document.querySelector(".tabs-container").addEventListener("touchend", (e) => {
	touchEndX = e.changedTouches[0].screenX;
	handleSwipe();
});

function handleSwipe() {
	const delta = touchEndX - touchStartX;
	const activeTab = Array.from(tabButtons).findIndex((btn) => btn.classList.contains("active-tab"));

	if (Math.abs(delta) > swipeThreshold) {
		if (delta > 0 && activeTab > 0) {
			tabButtons[activeTab - 1].click();
		} else if (delta < 0 && activeTab < tabButtons.length - 1) {
			tabButtons[activeTab + 1].click();
		}
	}
}

document.addEventListener("keydown", (e) => {
	const activeIndex = Array.from(tabButtons).findIndex((btn) => btn.classList.contains("active-tab"));

	if (e.key === "ArrowLeft" && activeIndex > 0) {
		tabButtons[activeIndex - 1].click();
	} else if (e.key === "ArrowRight" && activeIndex < tabButtons.length - 1) {
		tabButtons[activeIndex + 1].click();
	}
});
