style:
:root {
	--primary: #6366f1;
	--surface: #ffffff;
	--background: #f3f4f6;
	--text: #1f2937;
	--shadow: 4px 4px 12px rgba(0, 0, 0, 0.1), -4px -4px 12px rgba(255, 255, 255, 0.8);
}

[data-theme="dark"] {
	--primary: #818cf8;
	--surface: #1f2937;
	--background: #111827;
	--text: #f9fafb;
	--shadow: 4px 4px 12px rgba(0, 0, 0, 0.3), -4px -4px 12px rgba(255, 255, 255, 0.05);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	background: var(--background);
	color: var(--text);
	min-height: 100vh;
	display: grid;
	place-items: center;
	padding: 2rem;
}

.dashboard {
	background: var(--surface);
	width: 100%;
	max-width: 1200px;
	border-radius: 24px;
	box-shadow: var(--shadow);
	overflow: hidden;
}

.tab-header {
	padding: 1.5rem;
	position: relative;
	border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}

.tab-nav {
	display: flex;
	gap: 1rem;
	position: relative;
}

.tab-btn {
	padding: 1rem 2rem;
	border: none;
	background: transparent;
	color: var(--text);
	cursor: pointer;
	border-radius: 12px;
	display: flex;
	gap: 0.75rem;
	align-items: center;
	transition: all 0.3s ease;
	position: relative;
}

.tab-btn i {
	transition: transform 0.3s ease;
}

.tab-btn.active-tab {
	color: var(--primary);
}

.tab-btn.active-tab i {
	transform: scale(1.2);
}

.indicator {
	position: absolute;
	bottom: -2px;
	height: 3px;
	background: var(--primary);
	transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
	border-radius: 2px;
}

.tab-content {
	padding: 2rem;
	min-height: 400px;
	position: relative;
}

.loader {
	position: absolute;
	inset: 0;
	background: var(--surface);
	display: none;
	place-items: center;
	animation: fadeIn 0.3s ease;
}

.loader.active {
	display: grid;
}

.card-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
	gap: 1.5rem;
	opacity: 0;
	animation: fadeIn 0.3s 0.2s ease forwards;
}

.card {
	background: var(--background);
	padding: 1.5rem;
	border-radius: 16px;
	transition: transform 0.3s ease;
	position: relative;
	overflow: hidden;
}

.card::before {
	content: "";
	position: absolute;
	inset: 0;
	border: 2px solid var(--primary);
	opacity: 0;
	transition: opacity 0.3s ease;
	border-radius: 16px;
}

.card:hover {
	transform: translateY(-5px);
}

.card:hover::before {
	opacity: 0.2;
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	background: var(--surface);
	border: none;
	padding: 1rem;
	border-radius: 12px;
	cursor: pointer;
	box-shadow: var(--shadow);
}

@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateY(10px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@media (max-width: 768px) {
	.tab-nav {
		flex-direction: column;
	}

	.tab-btn {
		justify-content: center;
	}

	.indicator {
		display: none;
	}
}

script:
const themeToggle = document.querySelector(".theme-toggle");
const body = document.body;

function toggleTheme() {
	const isDark = body.getAttribute("data-theme") === "dark";
	body.setAttribute("data-theme", isDark ? "light" : "dark");
	themeToggle.innerHTML = isDark ? '<i class="fas fa-moon"></i>' : '<i class="fas fa-sun"></i>';
	localStorage.setItem("theme", isDark ? "light" : "dark");
}

const savedTheme = localStorage.getItem("theme");
if (savedTheme) {
	body.setAttribute("data-theme", savedTheme);
	themeToggle.innerHTML =
		savedTheme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
}

themeToggle.addEventListener("click", toggleTheme);

const tabButtons = document.querySelectorAll(".tab-btn");
const indicator = document.querySelector(".indicator");
const loader = document.querySelector(".loader");
const cardGrid = document.querySelector(".card-grid");

function updateIndicator(target) {
	const { width, left } = target.getBoundingClientRect();
	const navLeft = target.parentElement.getBoundingClientRect().left;

	indicator.style.width = `${width}px`;
	indicator.style.left = `${left - navLeft}px`;
}

function generateCards(tabId) {
	const templates = {
		dashboard: [
			{ title: "Revenue", icon: "dollar-sign", value: "$45,230" },
			{ title: "Users", icon: "users", value: "2,390" },
			{ title: "Conversion", icon: "percentage", value: "3.42%" },
		],
		projects: [
			{ title: "Web Platform", icon: "globe", value: "80%" },
			{ title: "Mobile App", icon: "mobile-alt", value: "65%" },
			{ title: "API Service", icon: "server", value: "95%" },
		],
		team: [
			{ title: "Designers", icon: "paint-brush", value: "12" },
			{ title: "Developers", icon: "code", value: "24" },
			{ title: "Managers", icon: "user-tie", value: "5" },
		],
	};

	cardGrid.innerHTML = templates[tabId]
		.map(
			(card) => `
				<div class="card">
					<h3><i class="fas fa-${card.icon}"></i> ${card.title}</h3>
					<p>${card.value}</p>
				</div>
			`,
		)
		.join("");
}

updateIndicator(document.querySelector(".active-tab"));
generateCards("dashboard");

tabButtons.forEach((button) => {
	button.addEventListener("click", async () => {
		loader.classList.add("active");

		await new Promise((resolve) => setTimeout(resolve, 800));

		tabButtons.forEach((btn) => btn.classList.remove("active-tab"));
		button.classList.add("active-tab");
		updateIndicator(button);
		generateCards(button.dataset.tab);

		loader.classList.remove("active");
	});
});

window.addEventListener("resize", () => {
	updateIndicator(document.querySelector(".active-tab"));
});

document.addEventListener("keydown", (e) => {
	const tabs = Array.from(tabButtons);
	const currentIndex = tabs.findIndex((t) => t.classList.contains("active-tab"));

	if (e.key === "ArrowRight" && currentIndex < tabs.length - 1) {
		tabs[currentIndex + 1].click();
	}
	if (e.key === "ArrowLeft" && currentIndex > 0) {
		tabs[currentIndex - 1].click();
	}
});
