style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

body {
	background: #f0f2f5;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 20px;
}

.tabs-container {
	background: white;
	width: 100%;
	max-width: 800px;
	border-radius: 20px;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.05);
	overflow: hidden;
}

.tabs-nav {
	display: flex;
	justify-content: space-between;
	position: relative;
	padding: 30px 40px;
	background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.tab-button {
	background: none;
	border: none;
	color: rgba(255, 255, 255, 0.7);
	cursor: pointer;
	display: flex;
	flex-direction: column;
	align-items: center;
	gap: 10px;
	position: relative;
	z-index: 1;
	transition: all 0.3s ease;
}

.tab-button.active {
	color: white;
	transform: translateY(-5px);
}

.tab-icon {
	width: 50px;
	height: 50px;
	background: rgba(255, 255, 255, 0.1);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 1.2rem;
	transition: all 0.3s ease;
}

.tab-button.active .tab-icon {
	background: white;
	color: #764ba2;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}

.tab-indicator {
	position: absolute;
	height: 4px;
	background: white;
	bottom: -2px;
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.tab-content {
	display: none;
	padding: 40px;
	animation: contentFade 0.4s ease;
}

.tab-content.active {
	display: block;
}

.tab-content h2 {
	color: #2d3748;
	margin-bottom: 20px;
	font-size: 1.8rem;
}

.tab-content p {
	color: #4a5568;
	line-height: 1.7;
	max-width: 600px;
}

@keyframes contentFade {
	from {
		opacity: 0;
		transform: translateY(10px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@media (max-width: 768px) {
	.tabs-nav {
		flex-direction: column;
		gap: 30px;
		padding: 30px 20px;
	}

	.tab-button {
		flex-direction: row;
		gap: 20px;
	}

	.tab-icon {
		width: 40px;
		height: 40px;
		font-size: 1rem;
	}

	.tab-indicator {
		display: none;
	}

	.tab-content {
		padding: 30px 20px;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const buttons = document.querySelectorAll(".tab-button");
	const contents = document.querySelectorAll(".tab-content");
	const indicator = document.querySelector(".tab-indicator");

	function updateIndicator(activeButton) {
		const rect = activeButton.getBoundingClientRect();
		const navRect = activeButton.parentElement.getBoundingClientRect();
		indicator.style.width = `${rect.width}px`;
		indicator.style.left = `${rect.left - navRect.left}px`;
	}

	buttons.forEach((button) => {
		button.addEventListener("click", () => {
			buttons.forEach((b) => b.classList.remove("active"));
			contents.forEach((c) => c.classList.remove("active"));

			button.classList.add("active");
			document.getElementById(button.dataset.target).classList.add("active");

			updateIndicator(button);
		});
	});

	updateIndicator(document.querySelector(".tab-button.active"));

	window.addEventListener("resize", () => {
		const activeButton = document.querySelector(".tab-button.active");
		updateIndicator(activeButton);
	});
});
