style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

body {
	background: #f8fafc;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 20px;
}

.tab-wrapper {
	background: #fff;
	width: 100%;
	max-width: 1000px;
	border-radius: 16px;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
	overflow: hidden;
}

.tab-header {
	display: flex;
	position: relative;
	padding: 24px;
	gap: 10px;
	border-bottom: 1px solid #e2e8f0;
}

.tab-item {
	background: none;
	border: none;
	padding: 12px 24px;
	cursor: pointer;
	color: #64748b;
	font-weight: 500;
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
	position: relative;
	display: flex;
	align-items: center;
	gap: 8px;
}

.tab-item::after {
	content: "";
	position: absolute;
	bottom: -2px;
	left: 0;
	width: 100%;
	height: 2px;
	background: #3b82f6;
	transform: scaleX(0);
	transition: transform 0.3s ease;
}

.tab-item:hover {
	color: #1e40af;
}

.tab-item.active {
	color: #1e40af;
}

.tab-item.active::after {
	transform: scaleX(1);
}

.tab-content {
	display: none;
	padding: 32px 24px;
	animation: slideIn 0.4s ease-out;
}

.tab-content.active {
	display: block;
}

.tab-content h2 {
	color: #1e293b;
	margin-bottom: 16px;
	font-size: 1.5rem;
}

.tab-content p {
	color: #475569;
	line-height: 1.6;
	max-width: 65ch;
}

@keyframes slideIn {
	from {
		opacity: 0;
		transform: translateY(10px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@media (max-width: 768px) {
	.tab-header {
		flex-direction: column;
		gap: 8px;
		padding: 16px;
	}

	.tab-item {
		justify-content: center;
		padding: 12px;
	}

	.tab-content {
		padding: 24px 16px;
	}
}

@media (max-width: 480px) {
	.tab-item span {
		display: none;
	}

	.tab-item i {
		font-size: 1.2rem;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const tabItems = document.querySelectorAll(".tab-item");
	const tabContents = document.querySelectorAll(".tab-content");

	tabItems.forEach((item) => {
		item.addEventListener("click", () => {
			tabItems.forEach((tab) => tab.classList.remove("active"));
			tabContents.forEach((content) => content.classList.remove("active"));

			item.classList.add("active");
			document.getElementById(item.dataset.target).classList.add("active");
		});
	});

	window.addEventListener("resize", () => {
		const activeTab = document.querySelector(".tab-item.active");
		activeTab.scrollIntoView({ behavior: "smooth", block: "nearest" });
	});
});
