style:
:root {
    --gold: linear-gradient(135deg, #ffd700 0%, #d4af37 100%);
    --velvet: #2a0a4a;
    --silk: #f5f5f5;
    --shadow: 0 12px 24px rgba(0, 0, 0, 0.15);
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: "Playfair Display", serif;
}

body {
    background: var(--velvet);
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 2rem;
}

.luxury-tabs {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(16px);
    border-radius: 24px;
    width: 100%;
    max-width: 1200px;
    box-shadow: var(--shadow);
    border: 1px solid rgba(255, 215, 0, 0.1);
    overflow: hidden;
}

.tabs-nav {
    display: flex;
    gap: 1rem;
    padding: 2rem;
    position: relative;
}

.tab-btn {
    padding: 1.5rem 3rem;
    border: none;
    background: none;
    color: rgba(255, 255, 255, 0.7);
    font-size: 1.2rem;
    cursor: pointer;
    position: relative;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    border-radius: 12px;
    display: flex;
    align-items: center;
    gap: 1rem;
}

.tab-btn::before {
    content: "";
    position: absolute;
    inset: 0;
    background: var(--gold);
    opacity: 0;
    border-radius: 12px;
    transition: opacity 0.3s ease;
}

.tab-btn:hover {
    color: white;
    transform: translateY(-3px);
}

.tab-btn.active {
    color: white;
    text-shadow: 0 0 8px rgba(255, 215, 0, 0.3);
}

.tab-btn.active::before {
    opacity: 0.15;
}

.tab-btn i {
    filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.2));
}

.gold-border {
    position: absolute;
    bottom: 0;
    height: 3px;
    background: var(--gold);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 0 0 12px rgba(255, 215, 0, 0.3);
}

.tab-content {
    padding: 3rem;
    display: none;
    min-height: 400px;
    position: relative;
    opacity: 0;
    animation: contentAppear 0.6s ease forwards;
}

.tab-content.active {
    display: block;
}

.luxury-card {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 215, 0, 0.1);
    border-radius: 16px;
    padding: 2rem;
    margin-bottom: 2rem;
    transition: transform 0.4s ease;
    backdrop-filter: blur(8px);
}

.luxury-card:hover {
    transform: translateY(-5px) rotate(1deg);
}

.luxury-card h3 {
    background: var(--gold);
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    font-size: 1.5rem;
    margin-bottom: 1rem;
}

.luxury-card p {
    color: rgba(255, 255, 255, 0.8);
    line-height: 1.6;
}

@keyframes contentAppear {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@media (max-width: 768px) {
    .tabs-nav {
        flex-direction: column;
        padding: 1rem;
    }

    .tab-btn {
        justify-content: center;
        padding: 1rem;
    }
}

script:
const tabButtons = document.querySelectorAll(".tab-btn");
const contents = document.querySelectorAll(".tab-content");
const goldBorder = document.querySelector(".gold-border");

function updateBorder(activeTab) {
    const { offsetLeft, offsetWidth } = activeTab;
    goldBorder.style.left = `${offsetLeft}px`;
    goldBorder.style.width = `${offsetWidth}px`;
}

function activateTab(tab) {
    tabButtons.forEach((t) => t.classList.remove("active"));
    contents.forEach((c) => c.classList.remove("active"));

    tab.classList.add("active");
    document.getElementById(tab.dataset.tab).classList.add("active");
    updateBorder(tab);
}

// Initialize
activateTab(document.querySelector(".tab-btn.active"));

// Event Listeners
tabButtons.forEach((tab) => {
    tab.addEventListener("click", () => activateTab(tab));
});

// Resize Handler
window.addEventListener("resize", () => {
    updateBorder(document.querySelector(".tab-btn.active"));
});

// Keyboard Navigation
document.addEventListener("keydown", (e) => {
    const activeTab = document.querySelector(".tab-btn.active");
    const tabIndex = Array.from(tabButtons).indexOf(activeTab);

    if (e.key === "ArrowRight" && tabIndex < tabButtons.length - 1) {
        activateTab(tabButtons[tabIndex + 1]);
    }
    if (e.key === "ArrowLeft" && tabIndex > 0) {
        activateTab(tabButtons[tabIndex - 1]);
    }
});
