style:
/* Reset styles */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: background 0.3s, color 0.3s;
}

body {
	font-family: Arial, sans-serif;
	background: var(--bg);
	color: var(--text);
	display: flex;
	height: 100vh;
}

/* Light & Dark Theme Variables */
:root {
	--bg: #f5f5f5;
	--text: #333;
	--sidebar-bg: #fff;
	--content-bg: #fff;
	--border: #ddd;
	--primary: #3498db;
	--hover: rgba(0, 0, 0, 0.1);
}

.dark-mode {
	--bg: #1e1e1e;
	--text: #f5f5f5;
	--sidebar-bg: #292929;
	--content-bg: #1e1e1e;
	--border: #444;
	--primary: #4db8ff;
	--hover: rgba(255, 255, 255, 0.1);
}

/* Sidebar Layout */
.sidebar {
	width: 250px;
	background: var(--sidebar-bg);
	padding: 20px;
	display: flex;
	flex-direction: column;
	align-items: center;
	border-right: 1px solid var(--border);
	box-shadow: 2px 0 10px rgba(0, 0, 0, 0.1);
}

/* Theme Toggle Button */
.theme-toggle {
	background: var(--content-bg);
	border: 1px solid var(--border);
	padding: 8px 12px;
	border-radius: 20px;
	cursor: pointer;
	font-size: 16px;
	color: var(--text);
	display: flex;
	align-items: center;
	gap: 8px;
	margin-bottom: 20px;
}

.theme-toggle:hover {
	background: var(--hover);
}

/* Tab Navigation */
.tab-header {
	display: flex;
	flex-direction: column;
	width: 100%;
}

.tab-header button {
	background: none;
	border: none;
	color: var(--text);
	padding: 15px;
	font-size: 18px;
	text-align: left;
	cursor: pointer;
	width: 100%;
	display: flex;
	align-items: center;
	gap: 10px;
	transition: 0.3s;
}

.tab-header button i {
	font-size: 20px;
}

.tab-header button.active {
	background: var(--primary);
	color: #fff;
	border-radius: 5px;
}

.tab-header button:hover {
	background: var(--hover);
}

/* Main Content */
.main-content {
	flex: 1;
	background: var(--content-bg);
	padding: 40px;
	display: flex;
	align-items: center;
	justify-content: center;
}

.tab-content > div {
	display: none;
	text-align: center;
	animation: fadeIn 0.3s ease-in-out;
}

.tab-content > div.active {
	display: block;
}

@keyframes fadeIn {
	from {
		opacity: 0;
	}
	to {
		opacity: 1;
	}
}

/* Responsive */
@media (max-width: 768px) {
	body {
		flex-direction: column;
	}

	.sidebar {
		width: 100%;
		flex-direction: row;
		justify-content: space-around;
		padding: 10px;
		box-shadow: none;
		border-right: none;
		border-bottom: 1px solid var(--border);
	}

	.tab-header {
		flex-direction: row;
		width: auto;
	}

	.tab-header button {
		font-size: 16px;
		padding: 10px;
	}
}

script:
document.addEventListener("DOMContentLoaded", function () {
	const tabButtons = document.querySelectorAll(".tab-header button");
	const tabPanels = document.querySelectorAll(".tab-content > div");
	const themeToggle = document.querySelector(".theme-toggle");
	const body = document.body;

	// Handle Tab Switching
	tabButtons.forEach((button) => {
		button.addEventListener("click", function () {
			tabButtons.forEach((btn) => btn.classList.remove("active"));
			tabPanels.forEach((panel) => panel.classList.remove("active"));

			this.classList.add("active");
			const tabId = this.getAttribute("data-tab");
			document.getElementById(tabId).classList.add("active");
		});
	});

	// Theme Toggle Function
	themeToggle.addEventListener("click", function () {
		body.classList.toggle("dark-mode");

		if (body.classList.contains("dark-mode")) {
			themeToggle.innerHTML = '<i class="fa-solid fa-sun"></i> Light Mode';
		} else {
			themeToggle.innerHTML = '<i class="fa-solid fa-moon"></i> Dark Mode';
		}
	});
});
