style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
}

body {
	background-color: #f8f9fa;
	padding: 40px 0;
}

.testimonial-section {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}

.section-title {
	text-align: center;
	margin-bottom: 50px;
}

.section-title h2 {
	font-size: 2.5rem;
	color: #2d3436;
	margin-bottom: 15px;
}

.section-title p {
	color: #636e72;
	font-size: 1.1rem;
}

.testimonial-container {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 30px;
}

.testimonial-card {
	background: white;
	padding: 30px;
	border-radius: 15px;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease;
}

.testimonial-card:hover {
	transform: translateY(-5px);
}

.testimonial-content {
	margin-bottom: 20px;
	color: #636e72;
	line-height: 1.6;
	font-size: 1rem;
}

.testimonial-author {
	display: flex;
	align-items: center;
}

.author-img {
	width: 60px;
	height: 60px;
	border-radius: 50%;
	margin-right: 15px;
	object-fit: cover;
}

.author-info h4 {
	color: #2d3436;
	margin-bottom: 5px;
	font-size: 1.1rem;
}

.author-info p {
	color: #636e72;
	font-size: 0.9rem;
}

.quote-icon {
	color: #0984e3;
	font-size: 2.5rem;
	margin-bottom: 15px;
	opacity: 0.7;
}

@media (max-width: 768px) {
	.testimonial-container {
		grid-template-columns: 1fr;
	}

	.section-title h2 {
		font-size: 2rem;
	}
}

script:
const testimonials = [
	{
		quote: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam voluptatem sed doloribus explicabo nulla deleniti.",
		author: "John Smith",
		role: "CEO at Tech Corp",
		image: "https://picsum.photos/200?random=1",
	},
	{
		quote: "Dolor sit amet consectetur adipisicing elit. Quisquam voluptatem sed doloribus explicabo nulla deleniti.",
		author: "Sarah Johnson",
		role: "Marketing Director",
		image: "https://picsum.photos/200?random=2",
	},
	{
		quote: "Consectetur adipisicing elit. Quisquam voluptatem sed doloribus explicabo nulla deleniti.",
		author: "Michael Chen",
		role: "Product Manager",
		image: "https://picsum.photos/200?random=3",
	},
];

function generateTestimonials() {
	const container = document.getElementById("testimonialContainer");

	testimonials.forEach((testimonial) => {
		const testimonialHTML = `
			<div class="testimonial-card">
				<div class="quote-icon">â€œ</div>
				<p class="testimonial-content">${testimonial.quote}</p>
				<div class="testimonial-author">
					<img src="${testimonial.image}" alt="${testimonial.author}" class="author-img">
					<div class="author-info">
						<h4>${testimonial.author}</h4>
						<p>${testimonial.role}</p>
					</div>
				</div>
			</div>
		`;

		container.insertAdjacentHTML("beforeend", testimonialHTML);
	});
}

// Generate testimonials on page load
generateTestimonials();
