style:
:root {
    --primary: #8b5cf6;
    --bg: #ffffff;
    --text: #1f2937;
    --card-bg: #f8fafc;
    --border: #e2e8f0;
    --shadow: rgba(0, 0, 0, 0.1);
}

.dark-mode {
    --primary: #a78bfa;
    --bg: #0f172a;
    --text: #f8fafc;
    --card-bg: rgba(30, 41, 59, 0.5);
    --border: #334155;
    --shadow: rgba(0, 0, 0, 0.3);
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: "Inter", system-ui, sans-serif;
}

body {
    background: var(--bg);
    color: var(--text);
    line-height: 1.6;
    padding: 4rem 0;
    transition: background 0.4s ease;
}

.theme-switch {
    position: fixed;
    top: 2rem;
    right: 2rem;
    width: 3rem;
    height: 3rem;
    border-radius: 50%;
    background: var(--card-bg);
    border: 2px solid var(--border);
    cursor: pointer;
    display: grid;
    place-items: center;
    z-index: 1000;
}

.container {
    max-width: 1280px;
    margin: 0 auto;
    padding: 0 2rem;
}

.section-header {
    text-align: center;
    margin-bottom: 4rem;
    position: relative;
}

.section-title {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    background: linear-gradient(to right, var(--primary), #7c3aed);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

.testimonials-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    position: relative;
}

.testimonial-card {
    background: var(--card-bg);
    padding: 2rem;
    border-radius: 1.5rem;
    border: 1px solid var(--border);
    backdrop-filter: blur(10px);
    transform-style: preserve-3d;
    position: relative;
    overflow: hidden;
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.testimonial-card::before {
    content: "";
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(45deg, transparent, var(--primary), transparent);
    transform: rotate(45deg);
    transition: all 0.4s ease;
    opacity: 0.1;
}

.testimonial-card:hover {
    transform: translateY(-0.5rem) rotateX(5deg) rotateY(5deg);
    box-shadow: 0 1rem 2rem var(--shadow);
}

.testimonial-card:hover::before {
    transform: rotate(45deg) translate(20%, 20%);
}

.quote-content {
    position: relative;
    z-index: 2;
}

.quote-icon {
    font-size: 3rem;
    color: var(--primary);
    opacity: 0.2;
    margin-bottom: 1rem;
}

.testimonial-text {
    font-size: 1rem;
    margin-bottom: 1.5rem;
    position: relative;
}

.author-info {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin-top: 1.5rem;
}

.author-img {
    width: 3.5rem;
    height: 3.5rem;
    border-radius: 50%;
    object-fit: cover;
    border: 2px solid var(--primary);
}

.author-details h3 {
    font-size: 1.1rem;
    margin-bottom: 0.25rem;
}

.author-details p {
    color: var(--primary);
    font-size: 0.9rem;
}

@media (max-width: 768px) {
    .section-title {
        font-size: 2rem;
    }

    .testimonials-grid {
        grid-template-columns: 1fr;
    }
}

script:
const testimonials = [
    {
        quote: "This platform has revolutionized our workflow. The efficiency gains are beyond anything we imagined possible.",
        author: "Alexandra Smith",
        role: "CTO at FutureTech",
        image: "https://picsum.photos/200?random=21"
    },
    {
        quote: "An exceptional blend of innovation and usability. Our team adopted it faster than any other tool we've tried.",
        author: "Michael Chen",
        role: "Product Director",
        image: "https://picsum.photos/200?random=22"
    },
    {
        quote: "The results speak for themselves - 150% increase in productivity within the first quarter of implementation.",
        author: "Sarah Johnson",
        role: "Operations Lead",
        image: "https://picsum.photos/200?random=23"
    },
    {
        quote: "A game-changing solution that has set new standards in our industry. Worth every penny invested.",
        author: "David Wilson",
        role: "Startup Founder",
        image: "https://picsum.photos/200?random=24"
    }
];

// Theme Toggle
const themeSwitch = document.getElementById("themeSwitch");
let isDark = localStorage.getItem("theme") === "dark";

function updateTheme() {
    document.body.classList.toggle("dark-mode", isDark);
    localStorage.setItem("theme", isDark ? "dark" : "light");
    themeSwitch.textContent = isDark ? "ðŸŒž" : "ðŸŒ“";
}

themeSwitch.addEventListener("click", () => {
    isDark = !isDark;
    updateTheme();
});

// Initialize theme
updateTheme();

// Generate Testimonials
function generateTestimonials() {
    const grid = document.getElementById("testimonialsGrid");

    testimonials.forEach((testimonial) => {
        const card = document.createElement("div");
        card.className = "testimonial-card";
        card.innerHTML = `
                    <div class="quote-content">
                        <div class="quote-icon">â€œ</div>
                        <p class="testimonial-text">${testimonial.quote}</p>
                        <div class="author-info">
                            <img src="${testimonial.image}" alt="${testimonial.author}" class="author-img">
                            <div class="author-details">
                                <h3>${testimonial.author}</h3>
                                <p>${testimonial.role}</p>
                            </div>
                        </div>
                    </div>
                `;
        grid.appendChild(card);
    });
}

// Initialize
generateTestimonials();
