style:
:root {
    --primary-color: #4f46e5;
    --secondary-color: #818cf8;
    --bg-color: #ffffff;
    --text-color: #1f2937;
    --card-bg: #f8fafc;
    --transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

[data-theme="dark"] {
    --bg-color: #0f172a;
    --text-color: #e2e8f0;
    --card-bg: #1e293b;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: var(--transition);
}

body {
    font-family: "Inter", system-ui, -apple-system, sans-serif;
    background-color: var(--bg-color);
    color: var(--text-color);
    line-height: 1.6;
}

.theme-toggle {
    position: fixed;
    top: 2rem;
    right: 2rem;
    width: 3rem;
    height: 3rem;
    border: none;
    border-radius: 50%;
    background: var(--card-bg);
    color: var(--text-color);
    cursor: pointer;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
    z-index: 1000;
    display: grid;
    place-items: center;
}

.testimonial-section {
    padding: 6rem 1rem;
    max-width: 1200px;
    margin: 0 auto;
    position: relative;
}

.timeline {
    position: absolute;
    left: 50%;
    width: 4px;
    height: 100%;
    background: var(--secondary-color);
    transform: translateX(-50%);
}

.testimonial-container {
    position: relative;
    display: flex;
    flex-direction: column;
    gap: 4rem;
}

.testimonial-card {
    width: calc(50% - 4rem);
    background: var(--card-bg);
    border-radius: 1rem;
    padding: 2rem;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
    position: relative;
    opacity: 0;
    transform: translateY(50px);
}

.testimonial-card:nth-child(even) {
    margin-left: auto;
}

.testimonial-card::before {
    content: "";
    position: absolute;
    top: 24px;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: var(--primary-color);
}

.testimonial-card:nth-child(odd)::before {
    right: -32px;
}

.testimonial-card:nth-child(even)::before {
    left: -32px;
}

.testimonial-card.visible {
    opacity: 1;
    transform: translateY(0);
}

.user-info {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin-bottom: 1.5rem;
}

.user-image {
    width: 56px;
    height: 56px;
    border-radius: 50%;
    object-fit: cover;
    border: 2px solid var(--primary-color);
}

.user-details {
    flex-grow: 1;
}

.user-name {
    color: var(--primary-color);
    font-weight: 600;
    margin-bottom: 0.25rem;
}

.user-role {
    color: #64748b;
    font-size: 0.9rem;
}

.testimonial-text {
    font-size: 1.1rem;
    position: relative;
    padding-left: 1.5rem;
}

.testimonial-text::before {
    content: "â€œ";
    position: absolute;
    left: 0;
    top: -0.5rem;
    font-size: 3rem;
    color: var(--primary-color);
    opacity: 0.3;
}

@media (max-width: 768px) {
    .timeline {
        left: 1rem;
    }

    .testimonial-card {
        width: calc(100% - 4rem);
        margin-left: 4rem !important;
    }

    .testimonial-card::before {
        left: -32px !important;
        right: auto !important;
    }
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(50px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

script:
// Theme Management
const themeToggle = document.querySelector(".theme-toggle");
const body = document.body;

function toggleTheme() {
    const isDark = body.getAttribute("data-theme") === "dark";
    body.setAttribute("data-theme", isDark ? "light" : "dark");
    localStorage.setItem("theme", isDark ? "light" : "dark");
    themeToggle.textContent = isDark ? "ðŸŒ“" : "ðŸŒž";
}

// Initialize theme
const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme);
themeToggle.textContent = savedTheme === "dark" ? "ðŸŒž" : "ðŸŒ“";
themeToggle.addEventListener("click", toggleTheme);

// Scroll Animation
const cards = document.querySelectorAll(".testimonial-card");

function checkScroll() {
    cards.forEach((card) => {
        const cardTop = card.getBoundingClientRect().top;
        const cardBottom = card.getBoundingClientRect().bottom;

        if (cardTop < window.innerHeight * 0.8 && cardBottom > 0) {
            card.classList.add("visible");
        }
    });
}

window.addEventListener("scroll", checkScroll);
window.addEventListener("resize", checkScroll);
checkScroll();
