style:
:root {
    --primary: #7c3aed;
    --bg: #f8fafc;
    --text: #1e293b;
    --card-bg: #ffffff;
    --border: #e2e8f0;
    --shadow: rgba(0, 0, 0, 0.1);
}

.dark-mode {
    --primary: #a78bfa;
    --bg: #0f172a;
    --text: #f8fafc;
    --card-bg: #1e293b;
    --border: #334155;
    --shadow: rgba(0, 0, 0, 0.3);
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: "Inter", system-ui, sans-serif;
    transition: 0.3s ease;
}

body {
    background: var(--bg);
    color: var(--text);
    line-height: 1.6;
    padding: 4rem 0;
}

.theme-switch {
    position: fixed;
    top: 2rem;
    right: 2rem;
    width: 3.5rem;
    height: 2rem;
    background: var(--card-bg);
    border-radius: 2rem;
    cursor: pointer;
    border: 2px solid var(--border);
}

.theme-switch::before {
    content: "";
    position: absolute;
    top: 0.15rem;
    left: 0.25rem;
    width: 1.5rem;
    height: 1.5rem;
    background: var(--primary);
    border-radius: 50%;
    transition: 0.3s;
}

.dark-mode .theme-switch::before {
    transform: translateX(1.3rem);
}

.container {
    max-width: 1280px;
    margin: 0 auto;
    padding: 0 2rem;
}

.header {
    text-align: center;
    margin-bottom: 4rem;
}

.header h1 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    background: linear-gradient(to right, var(--primary), #7c3aed);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

.testimonials-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
}

.testimonial {
    background: var(--card-bg);
    padding: 2rem;
    border-radius: 1rem;
    border: 1px solid var(--border);
    box-shadow: 0 1rem 2rem var(--shadow);
}

.testimonial:hover {
    transform: translateY(-0.5rem);
}

.quote {
    font-size: 1.1rem;
    color: var(--text);
    margin-bottom: 1.5rem;
    position: relative;
}

.quote::before {
    content: "“";
    position: absolute;
    top: -1.5rem;
    left: -0.5rem;
    font-size: 4rem;
    color: var(--primary);
    opacity: 0.2;
}

.author {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.author-img {
    width: 3.5rem;
    height: 3.5rem;
    border-radius: 50%;
    object-fit: cover;
    border: 2px solid var(--primary);
}

.author-info h3 {
    color: var(--text);
    margin-bottom: 0.25rem;
}

.author-info p {
    color: var(--primary);
    font-size: 0.9rem;
}

.rating {
    display: flex;
    gap: 0.25rem;
    margin-bottom: 1rem;
}

.star {
    color: #fbbf24;
    font-size: 1.2rem;
}

@media (max-width: 768px) {
    body {
        padding: 2rem 0;
    }

    .header h1 {
        font-size: 2rem;
    }

    .testimonials-grid {
        grid-template-columns: 1fr;
    }
}

script:
const testimonials = [
    {
        quote: "An absolute game-changer! The results we've seen in just 3 months are incredible.",
        author: "Alex Turner",
        role: "Startup Founder",
        image: "https://picsum.photos/200?random=9",
        rating: 5
    },
    {
        quote: "Their attention to detail and creative approach helped us double our engagement.",
        author: "Samantha Lee",
        role: "Marketing Director",
        image: "https://picsum.photos/200?random=10",
        rating: 4
    },
    {
        quote: "A truly transformative partnership that exceeded all expectations.",
        author: "James Wilson",
        role: "CTO",
        image: "https://picsum.photos/200?random=11",
        rating: 5
    },
    {
        quote: "The best investment we've made in our digital strategy to date.",
        author: "Maria Gonzalez",
        role: "E-commerce Manager",
        image: "https://picsum.photos/200?random=12",
        rating: 5
    }
];

// Theme Toggle
const themeSwitch = document.getElementById("themeSwitch");
let isDark = localStorage.getItem("theme") === "dark";

function setTheme() {
    document.body.classList.toggle("dark-mode", isDark);
    localStorage.setItem("theme", isDark ? "dark" : "light");
}

themeSwitch.addEventListener("click", () => {
    isDark = !isDark;
    setTheme();
});

// Set initial theme
setTheme();

// Generate Testimonials
function generateStars(rating) {
    return "★".repeat(rating) + "☆".repeat(5 - rating);
}

function renderTestimonials() {
    const grid = document.getElementById("testimonialsGrid");
    grid.innerHTML = testimonials
        .map(
            (testimonial) => `
                <article class="testimonial">
                    <div class="rating">
                        ${generateStars(testimonial.rating)
                            .split("")
                            .map(
                                (star) => `
                            <span class="star">${star}</span>
                        `
                            )
                            .join("")}
                    </div>
                    <p class="quote">${testimonial.quote}</p>
                    <div class="author">
                        <img src="${testimonial.image}" alt="${testimonial.author}" class="author-img">
                        <div class="author-info">
                            <h3>${testimonial.author}</h3>
                            <p>${testimonial.role}</p>
                        </div>
                    </div>
                </article>
            `
        )
        .join("");
}

// Initialize
renderTestimonials();
