style:
:root {
	--primary: #10b981;
	--bg-light: #f9fafb;
	--text-light: #1f2937;
	--bg-dark: #111827;
	--text-dark: #f9fafb;
	--card-bg-light: #ffffff;
	--card-bg-dark: #1f2937;
	--transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, sans-serif;
}

body {
	background: var(--bg-light);
	color: var(--text-light);
	line-height: 1.5;
	min-height: 100vh;
	padding: 4rem 0;
	transition: var(--transition);
}

body.dark {
	background: var(--bg-dark);
	color: var(--text-dark);
}

.theme-toggle {
	position: fixed;
	top: 2rem;
	right: 2rem;
	background: var(--card-bg-light);
	border: none;
	padding: 0.75rem;
	border-radius: 50%;
	cursor: pointer;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
	transition: var(--transition);
}

body.dark .theme-toggle {
	background: var(--card-bg-dark);
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.3);
}

.container {
	max-width: 1280px;
	margin: 0 auto;
	padding: 0 2rem;
}

.section-header {
	text-align: center;
	margin-bottom: 4rem;
}

.section-title {
	font-size: 2.5rem;
	margin-bottom: 1rem;
	position: relative;
	display: inline-block;
}

.section-title::after {
	content: "";
	position: absolute;
	bottom: -0.5rem;
	left: 50%;
	transform: translateX(-50%);
	width: 60px;
	height: 3px;
	background: var(--primary);
}

.testimonials-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
}

.testimonial-card {
	background: var(--card-bg-light);
	padding: 2rem;
	border-radius: 1rem;
	box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
	transition: var(--transition);
	position: relative;
	overflow: hidden;
}

body.dark .testimonial-card {
	background: var(--card-bg-dark);
	box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.3);
}

.testimonial-card::before {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 4px;
	background: var(--primary);
	transform: scaleX(0);
	transition: var(--transition);
}

.testimonial-card:hover::before {
	transform: scaleX(1);
}

.testimonial-content {
	margin-bottom: 1.5rem;
	position: relative;
}

.testimonial-text {
	font-size: 1rem;
	color: var(--text-light);
	margin-bottom: 1rem;
}

body.dark .testimonial-text {
	color: var(--text-dark);
}

.author-info {
	display: flex;
	align-items: center;
	gap: 1rem;
}

.author-img {
	width: 56px;
	height: 56px;
	border-radius: 50%;
	object-fit: cover;
	border: 2px solid var(--primary);
	padding: 2px;
}

.author-details h3 {
	font-size: 1.1rem;
	margin-bottom: 0.25rem;
}

.author-details p {
	font-size: 0.9rem;
	color: var(--primary);
}

.quote-icon {
	position: absolute;
	top: -1rem;
	left: -0.5rem;
	font-size: 4rem;
	color: var(--primary);
	opacity: 0.1;
	z-index: -1;
}

@media (max-width: 768px) {
	.container {
		padding: 0 1rem;
	}

	.section-title {
		font-size: 2rem;
	}

	.testimonials-grid {
		grid-template-columns: 1fr;
	}
}

script:
const testimonials = [
	{
		text: "This platform has completely transformed how we manage our projects. The efficiency gains are incredible!",
		author: "Sarah Johnson",
		role: "CEO at TechSolutions",
		image: "https://picsum.photos/200?random=13",
	},
	{
		text: "Outstanding service and support. Their team went above and beyond to meet our unique requirements.",
		author: "Michael Chen",
		role: "Product Manager",
		image: "https://picsum.photos/200?random=14",
	},
	{
		text: "The results we've achieved in just 3 months have exceeded all our expectations. Truly remarkable!",
		author: "Emma Wilson",
		role: "Marketing Director",
		image: "https://picsum.photos/200?random=15",
	},
	{
		text: "A game-changing solution that streamlined our operations and boosted team productivity by 200%.",
		author: "David Martinez",
		role: "Operations Lead",
		image: "https://picsum.photos/200?random=16",
	},
];

// Theme Toggle
const themeToggle = document.getElementById("themeToggle");
const body = document.body;

function toggleTheme() {
	body.classList.toggle("dark");
	localStorage.setItem("theme", body.classList.contains("dark") ? "dark" : "light");
	themeToggle.textContent = body.classList.contains("dark") ? "ðŸŒž" : "ðŸŒ“";
}

// Load saved theme
const savedTheme = localStorage.getItem("theme");
if (savedTheme === "dark") {
	body.classList.add("dark");
	themeToggle.textContent = "ðŸŒž";
}

themeToggle.addEventListener("click", toggleTheme);

// Generate Testimonials
function generateTestimonials() {
	const container = document.getElementById("testimonialsContainer");

	testimonials.forEach((testimonial) => {
		const card = document.createElement("div");
		card.className = "testimonial-card";
		card.innerHTML = `
			<div class="testimonial-content">
				<div class="quote-icon">â€œ</div>
				<p class="testimonial-text">${testimonial.text}</p>
				<div class="author-info">
					<img src="${testimonial.image}" alt="${testimonial.author}" class="author-img">
					<div class="author-details">
						<h3>${testimonial.author}</h3>
						<p>${testimonial.role}</p>
					</div>
				</div>
			</div>
		`;

		container.appendChild(card);
	});
}

generateTestimonials();
