style:
:root {
	--primary-color: #6366f1;
	--secondary-color: #a855f7;
	--bg-color: #ffffff;
	--text-color: #1f2937;
	--card-bg: #f8fafc;
	--transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

[data-theme="dark"] {
	--bg-color: #0f172a;
	--text-color: #e2e8f0;
	--card-bg: #1e293b;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: var(--transition);
}

body {
	font-family: "Inter", system-ui, -apple-system, sans-serif;
	background-color: var(--bg-color);
	color: var(--text-color);
	line-height: 1.6;
}

.theme-toggle {
	position: fixed;
	top: 2rem;
	right: 2rem;
	width: 3rem;
	height: 3rem;
	border: none;
	border-radius: 50%;
	background: var(--card-bg);
	color: var(--text-color);
	cursor: pointer;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
	z-index: 1000;
	display: grid;
	place-items: center;
}

.testimonial-section {
	padding: 6rem 1rem;
	max-width: 1400px;
	margin: 0 auto;
	min-height: 100vh;
	display: flex;
	align-items: center;
}

.testimonial-container {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 3rem;
	width: 100%;
}

.testimonial-card {
	background: var(--card-bg);
	border-radius: 1.5rem;
	padding: 2.5rem;
	position: relative;
	overflow: hidden;
	transform: translateY(0);
	box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
}

.testimonial-card:hover {
	transform: translateY(-1rem);
}

.testimonial-card::before {
	content: "";
	position: absolute;
	top: -1px;
	left: -1px;
	right: -1px;
	bottom: -1px;
	background: linear-gradient(45deg, var(--primary-color), var(--secondary-color));
	z-index: -1;
	opacity: 0;
	transition: opacity 0.3s ease;
}

.testimonial-card:hover::before {
	opacity: 0.1;
}

.quote-icon {
	font-size: 3rem;
	color: var(--primary-color);
	opacity: 0.3;
	margin-bottom: 1.5rem;
}

.testimonial-text {
	font-size: 1.1rem;
	margin-bottom: 2rem;
	position: relative;
	z-index: 1;
}

.user-info {
	display: flex;
	align-items: center;
	gap: 1rem;
}

.user-image {
	width: 56px;
	height: 56px;
	border-radius: 50%;
	object-fit: cover;
	border: 2px solid var(--primary-color);
}

.user-details {
	flex-grow: 1;
}

.user-name {
	color: var(--primary-color);
	font-weight: 600;
	margin-bottom: 0.25rem;
}

.user-role {
	color: #64748b;
	font-size: 0.9rem;
}

@media (max-width: 768px) {
	.testimonial-container {
		grid-template-columns: 1fr;
	}

	.testimonial-section {
		padding: 4rem 1rem;
	}
}

@keyframes float {
	0%,
	100% {
		transform: translateY(0);
	}
	50% {
		transform: translateY(-1rem);
	}
}

script:
// Theme Management
const themeToggle = document.querySelector(".theme-toggle");
const body = document.body;

function toggleTheme() {
	const isDark = body.getAttribute("data-theme") === "dark";
	body.setAttribute("data-theme", isDark ? "light" : "dark");
	localStorage.setItem("theme", isDark ? "light" : "dark");
	themeToggle.textContent = isDark ? "ðŸŒ“" : "ðŸŒž";
}

// Initialize theme
const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme);
themeToggle.textContent = savedTheme === "dark" ? "ðŸŒž" : "ðŸŒ“";
themeToggle.addEventListener("click", toggleTheme);

// Add floating animation on scroll
const cards = document.querySelectorAll(".testimonial-card");

function checkScroll() {
	cards.forEach((card) => {
		const cardTop = card.getBoundingClientRect().top;
		if (cardTop < window.innerHeight * 0.8) {
			card.style.animation = "float 4s ease-in-out infinite";
		}
	});
}

window.addEventListener("scroll", checkScroll);
checkScroll(); // Initial check

// Add hover effect for mobile
cards.forEach((card) => {
	card.addEventListener("touchstart", () => {
		card.classList.add("hover");
	});

	card.addEventListener("touchend", () => {
		setTimeout(() => card.classList.remove("hover"), 500);
	});
});
