style:
/* Reset & Base Styles */
* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
}

body {
	background: #f9f9f9;
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
	font-family: sans-serif;
}

/* Toggle Container */
.toggle-container {
	position: relative;
	width: 120px;
	height: 60px;
}

.toggle-container input {
	opacity: 0;
	width: 0;
	height: 0;
}

/* The Toggle Track */
.toggle-label {
	display: block;
	width: 100%;
	height: 100%;
	background: linear-gradient(145deg, #e6e6e6, #ffffff);
	border-radius: 50px;
	box-shadow: 7px 7px 14px #bebebe, -7px -7px 14px #ffffff;
	cursor: pointer;
	transition: background 0.3s ease, box-shadow 0.3s ease;
	position: relative;
}

/* The Sliding Knob */
.toggle-label::after {
	content: "";
	position: absolute;
	top: 5px;
	left: 5px;
	width: 50px;
	height: 50px;
	background: linear-gradient(145deg, #ff6a00, #ee0979);
	border-radius: 50%;
	transition: transform 0.3s ease;
	box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
}

/* Toggle On State */
input:checked + .toggle-label::after {
	transform: translateX(60px);
}

input:checked + .toggle-label {
	background: linear-gradient(145deg, #ffecd2, #fcb69f);
	box-shadow: 7px 7px 14px rgba(0, 0, 0, 0.2), -7px -7px 14px rgba(255, 255, 255, 0.9);
}

/* Responsive Adjustments */
@media (max-width: 480px) {
	.toggle-container {
		width: 100px;
		height: 50px;
	}
	.toggle-label::after {
		width: 40px;
		height: 40px;
		top: 5px;
		left: 5px;
	}
	input:checked + .toggle-label::after {
		transform: translateX(50px);
	}
}
