style:
:root {
	--bg-color: #f5f7fb;
	--text-color: #1a1a1a;
	--primary-color: #6366f1;
	--tooltip-bg: #ffffff;
	--tooltip-text: #1a1a1a;
	--tooltip-border: #6366f1;
	--border-radius: 8px;
	--transition-speed: 0.2s;
	--shadow: 0 2px 12px rgba(0, 0, 0, 0.1);
}

.dark-theme {
	--bg-color: #0f172a;
	--text-color: #f8fafc;
	--primary-color: #818cf8;
	--tooltip-bg: #1e293b;
	--tooltip-text: #f8fafc;
	--tooltip-border: #818cf8;
}

* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	transition: all var(--transition-speed) ease;
}

body {
	font-family: "Inter", system-ui, sans-serif;
	background-color: var(--bg-color);
	color: var(--text-color);
	line-height: 1.5;
	padding: 2rem;
	min-height: 100vh;
}

.container {
	max-width: 800px;
	margin: 3rem auto;
	display: grid;
	gap: 1.5rem;
	grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
}

.tooltip-container {
	position: relative;
	display: flex;
	justify-content: center;
}

.demo-button {
	padding: 0.7rem 1.4rem;
	background: var(--primary-color);
	color: white;
	border: none;
	border-radius: 6px;
	cursor: pointer;
	font-size: 0.9rem;
	font-weight: 500;
	width: 100%;
	max-width: 140px;
	position: relative;
	overflow: hidden;
	transition: transform 0.2s;
}

.demo-button:hover {
	transform: translateY(-2px);
}

.tooltip {
	position: absolute;
	background: var(--tooltip-bg);
	color: var(--tooltip-text);
	padding: 0.8rem 1.2rem;
	border-radius: var(--border-radius);
	font-size: 0.85rem;
	pointer-events: none;
	opacity: 0;
	transition: opacity var(--transition-speed);
	z-index: 1000;
	box-shadow: var(--shadow);
	border: 2px solid var(--tooltip-border);
}

.tooltip::before {
	content: "";
	position: absolute;
	border-width: 8px;
	border-style: solid;
	filter: drop-shadow(0 2px 2px rgba(0, 0, 0, 0.1));
}

.tooltip-container:hover .tooltip {
	opacity: 1;
}

/* Top Position */
.tooltip[data-position="top"] {
	bottom: calc(100% + 12px);
	left: 50%;
	transform: translateX(-50%);
}
.tooltip[data-position="top"]::before {
	top: 100%;
	left: 50%;
	border-color: var(--tooltip-border) transparent transparent transparent;
	transform: translateX(-50%);
}

/* Bottom Position */
.tooltip[data-position="bottom"] {
	top: calc(100% + 12px);
	left: 50%;
	transform: translateX(-50%);
}
.tooltip[data-position="bottom"]::before {
	bottom: 100%;
	left: 50%;
	border-color: transparent transparent var(--tooltip-border) transparent;
	transform: translateX(-50%);
}

/* Left Position */
.tooltip[data-position="left"] {
	right: calc(100% + 12px);
	top: 50%;
	transform: translateY(-50%);
}
.tooltip[data-position="left"]::before {
	left: 100%;
	top: 50%;
	border-color: transparent transparent transparent var(--tooltip-border);
	transform: translateY(-50%);
}

/* Right Position */
.tooltip[data-position="right"] {
	left: calc(100% + 12px);
	top: 50%;
	transform: translateY(-50%);
}
.tooltip[data-position="right"]::before {
	right: 100%;
	top: 50%;
	border-color: transparent var(--tooltip-border) transparent transparent;
	transform: translateY(-50%);
}

.theme-toggle {
	position: fixed;
	top: 1.5rem;
	right: 1.5rem;
	padding: 0.75rem;
	background: var(--primary-color);
	color: white;
	border: none;
	border-radius: 8px;
	cursor: pointer;
	box-shadow: var(--shadow);
	font-size: 1rem;
}

h2 {
	text-align: center;
	margin-bottom: 3rem;
	color: var(--text-color);
	font-size: 1.8rem;
}

script:
const themeToggle = document.querySelector(".theme-toggle");
themeToggle.addEventListener("click", () => {
	document.body.classList.toggle("dark-theme");
	themeToggle.textContent = document.body.classList.contains("dark-theme") ? "Light Mode" : "Dark Mode";
});
