style:
:root {
	--bg-color: #f8f9fa;
	--text-color: #2d3436;
	--primary-color: #6c5ce7;
	--tooltip-bg: rgba(108, 92, 231, 0.95);
	--tooltip-text: #ffffff;
	--border-radius: 12px;
	--transition-speed: 0.3s;
	--shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.dark-theme {
	--bg-color: #1a1a1a;
	--text-color: #f8f9fa;
	--primary-color: #8475f2;
	--tooltip-bg: rgba(132, 117, 242, 0.95);
}

* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	transition: all var(--transition-speed) ease;
}

body {
	font-family: "Inter", system-ui, -apple-system, sans-serif;
	background-color: var(--bg-color);
	color: var(--text-color);
	line-height: 1.6;
	padding: 2rem;
	min-height: 100vh;
}

.container {
	max-width: 800px;
	margin: 2rem auto;
	display: grid;
	gap: 1.5rem;
	grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
}

.tooltip-container {
	position: relative;
	display: flex;
	justify-content: center;
}

.demo-button {
	padding: 0.75rem 1.5rem;
	background: linear-gradient(135deg, var(--primary-color) 0%, #7c6cf1 100%);
	color: white;
	border: none;
	border-radius: 8px;
	cursor: pointer;
	font-size: 0.9rem;
	width: 100%;
	max-width: 140px;
	box-shadow: var(--shadow);
	position: relative;
	overflow: hidden;
}

.demo-button::before {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: rgba(255, 255, 255, 0.1);
	opacity: 0;
	transition: opacity var(--transition-speed);
}

.demo-button:hover::before {
	opacity: 1;
}

.demo-button:hover {
	transform: translateY(-2px);
	box-shadow: 0 6px 12px rgba(108, 92, 231, 0.2);
}

.tooltip {
	position: absolute;
	background: var(--tooltip-bg);
	color: var(--tooltip-text);
	padding: 0.75rem 1.25rem;
	border-radius: 6px;
	font-size: 0.85rem;
	pointer-events: none;
	opacity: 0;
	transition: opacity var(--transition-speed), transform var(--transition-speed);
	white-space: nowrap;
	z-index: 1000;
	backdrop-filter: blur(4px);
	box-shadow: var(--shadow);
}

.tooltip::after {
	content: "";
	position: absolute;
	border-width: 6px;
	border-style: solid;
}

.tooltip-container:hover .tooltip {
	opacity: 1;
}

/* Tooltip positioning */
.tooltip[data-position="top"] {
	bottom: calc(100% + 12px);
	left: 50%;
	transform: translateX(-50%);
}

.tooltip[data-position="top"]::after {
	top: 100%;
	left: 50%;
	border-color: var(--tooltip-bg) transparent transparent transparent;
	transform: translateX(-50%);
}

.tooltip[data-position="bottom"] {
	top: calc(100% + 12px);
	left: 50%;
	transform: translateX(-50%);
}

.tooltip[data-position="bottom"]::after {
	bottom: 100%;
	left: 50%;
	border-color: transparent transparent var(--tooltip-bg) transparent;
	transform: translateX(-50%);
}

.tooltip[data-position="left"] {
	right: calc(100% + 12px);
	top: 50%;
	transform: translateY(-50%);
}

.tooltip[data-position="left"]::after {
	left: 100%;
	top: 50%;
	border-color: transparent transparent transparent var(--tooltip-bg);
	transform: translateY(-50%);
}

.tooltip[data-position="right"] {
	left: calc(100% + 12px);
	top: 50%;
	transform: translateY(-50%);
}

.tooltip[data-position="right"]::after {
	right: 100%;
	top: 50%;
	border-color: transparent var(--tooltip-bg) transparent transparent;
	transform: translateY(-50%);
}

.theme-toggle {
	position: fixed;
	top: 1.5rem;
	right: 1.5rem;
	padding: 0.75rem;
	background: var(--primary-color);
	color: white;
	border: none;
	border-radius: 50%;
	cursor: pointer;
	box-shadow: var(--shadow);
	width: 48px;
	height: 48px;
	display: flex;
	align-items: center;
	justify-content: center;
}

.theme-toggle:hover {
	transform: scale(1.1) rotate(15deg);
}

.direction-showcase {
	text-align: center;
	margin: 2rem 0;
	padding: 2rem;
	border-radius: var(--border-radius);
	background: rgba(108, 92, 231, 0.05);
}

h2 {
	margin-bottom: 2rem;
	color: var(--primary-color);
	font-weight: 600;
}

script:
// Theme toggle functionality
const themeToggle = document.querySelector(".theme-toggle");
themeToggle.addEventListener("click", () => {
	document.body.classList.toggle("dark-theme");
});
